INSERT INTO `movie_info` VALUES ('5450891', '泰坦尼克号 3D版', '莱昂纳多·迪卡普里奥,凯特·温丝莱特,比利·赞恩,凯西·贝茨,弗兰西丝·费舍,格劳瑞亚·斯图尔特,比尔·帕克斯顿,伯纳德·希尔,大卫·沃纳,维克多·加博,乔纳森·海德,苏茜·爱米斯,刘易斯·阿伯内西,尼古拉斯·卡斯柯恩,阿那托利·萨加洛维奇,丹尼·努齐,杰森·贝瑞,伊万·斯图尔特', '美国', '2012-04-10', '', 'https://img3.doubanio.com/view/photo/s_ratio_poster/public/p1408665952.jpg', '9.4', '50', '167967', 'https://movie.douban.com/subject/5450891/');
